import React from 'react';
import './Slide.css';

const Slide = ({ data }) => {
  const { type, title, subtitle, content, items, code, highlights } = data;

  return (
    <div className={`slide slide-${type}`}>
      <div className="slide-content">
        {title && <h1 className="slide-title">{title}</h1>}
        {subtitle && <h2 className="slide-subtitle">{subtitle}</h2>}
        
        {content && <p className="slide-text">{content}</p>}
        
        {items && (
          <ul className="slide-list">
            {items.map((item, index) => (
              <li key={index} className="slide-list-item">
                {item.icon && <span className="item-icon">{item.icon}</span>}
                <div>
                  {item.title && <strong>{item.title}</strong>}
                  {item.description && <p>{item.description}</p>}
                  {!item.title && !item.description && item}
                </div>
              </li>
            ))}
          </ul>
        )}
        
        {code && (
          <pre className="slide-code">
            <code>{code}</code>
          </pre>
        )}
        
        {highlights && (
          <div className="highlights">
            {highlights.map((highlight, index) => (
              <div key={index} className="highlight-box">
                <div className="highlight-number">{index + 1}</div>
                <div className="highlight-content">
                  <h3>{highlight.title}</h3>
                  <p>{highlight.description}</p>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Slide;
